<?phpnamespace App\Http\Controllers;use Illuminate\Http\Request;use Illuminate\Support\Facades\Auth;use Illuminate\Support\MessageBag;class AuthController extends Controller{    public function login(Request $request)    {        $email = $request['email'];        $password = $request['password'];        if (Auth::attempt(['email' => $email, 'password' => $password])) {            return redirect()->intended('/');        } else {            $errors = new MessageBag(['errorlogin' => 'Email hoặc mật khẩu không đúng']);            return redirect()->back()->withInput()->withErrors($errors);        }    }    public function logout()    {        Auth::logout();        return redirect()->intended('login');    }    public function checklogin(){        if(Auth::check()){            return view('backend.admin.dashboard.index');        }else{            return view('backend.login.index');        }    }}